<?php if (!defined('BASEPATH')) exit('No direct script access allowed'); class mdl_gajibulanan extends CI_Model {		var $tabelrecipient = 'recipient'; 	function __construct(){		parent::__construct();	}  		function form_get_all($count,$offset,$perpage,$filter){		$where = ""; 		// if(strlen($filter['txt_search'])>0){			// $where  = "where kodegajibulanan like '%".$filter['txt_search']."%'";		// }		if($count){			$query = "SELECT * from gajibulanan $where ";		}else{			$query = "SELECT * from gajibulanan $where order by kodegajibulanan asc OFFSET $offset ROWS FETCH NEXT $perpage ROWS ONLY";		}		 		return $this->db->query($query);	} 		function form_save($data,$kode){		if($kode!=""){			$this->db->where('kodegajibulanan', $kode);			return $this->db->update('gajibulanan', $data);		}else{			return $this->db->insert('gajibulanan', $data);			 		}	}	function form_delete($kode){				$query = "delete from gajibulanan where kodegajibulanan=".$kode; 		return $this->db->query($query);	}		function form_get_byid($kode){         $this->db->from($this->tabel);        $query = $this->db->get();         //cek apakah ada data        if ($query->num_rows() > 0) { //jika ada maka jalankan            return $query->result();        }    }}