tinymce.PluginManager.add('jwifm', function(editor, url) {
	
    // Add a button that opens a window
    editor.addButton('jwifm', {
        text: '',
        icon: true,
        image : url + '/images/folder.png',
        onclick: function() {  
        	$('#ModalFileManager').modal('show');            
        }
    });
 
});
